import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function Preview() {
  const [dept, setDept] = useState('');
  const [sem, setSem] = useState('');
  const [sect, setSect] = useState('');
  const [date, setDate] = useState('');
  const [users, setUsers] = useState([]);

  // Ensure Axios sends credentials
  axios.defaults.withCredentials = true;

  // Clear form fields
  const handleClear = () => {
    setDept('');
    setSem('');
    setSect('');
    setDate('');
  };

  // useEffect(() => {
  //   axios.get('http://localhost:3001/previewapi/preview')
  //     .then(response => setUsers(response.data))
  //     .catch(err => console.log(err));
  // }, []);

  // Form submit handler
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission
    axios.post('http://localhost:3001/previewapi/preview', { dept, sem, sect, date })
      .then(result => {
        console.log('Form submitted successfully:', result.data);
        // Refetch data to update the users list
        axios.get('http://localhost:3001/previewapi/preview')
          .then(response => setUsers(response.data))
          .catch(err => console.log('Error fetching updated users:', err));
        handleClear(); // Clear form fields after submission
      })
      .catch(err => {
        console.log('Error submitting form:', err);
      });
  };



 




  return (
    <div>
      <div className="container h-100">
        <div className="row d-flex justify-content-center align-items-center h-100 m-3">
          <div className="card-body ">
            <h1 className="mb-3 text-center">Welcome Sagar Raj</h1>
            <hr />
            <form onSubmit={handleSubmit}>
              <h2 className="mb-3 text-center">Student's Attendance Details</h2>
              <div className="row">
                <div className="col-3">
                  <div data-mdb-input-init className="form-outline mb-2">
                    <label className="form-label" for="typeNameX-2">Department</label>
                    <select className="form-select" aria-label="Default select example" value={dept} onChange={(e) => setDept(e.target.value)} required>
                      <option value="" disabled selected>Choose Dapartment</option>
                      <option value="Aero">Aero</option>
                      <option value="Aeronautical">Aeronautical</option>
                      <option value="BME">BME</option>
                      <option value="CSE">CSE</option>
                      <option value="IT">IT</option>

                    </select>
                  </div>
                </div>
                <div className="col-3">
                  <div data-mdb-input-init className="form-outline mb-2">
                    <label className="form-label" for="typeNameX-2">Semester</label>
                    <select className="form-select" aria-label="Default select example" value={sem} onChange={(e) => setSem(e.target.value)} required>
                      <option selected>Choose Your Semester</option>
                      <option value="I">I</option>
                      <option value="II">II</option>
                      <option value="III">III</option>
                      <option value="IV">IV</option>
                      <option value="V">V</option>
                      <option value="VI">VI</option>
                      <option value="VII">VII</option>
                      <option value="VIII">VIII</option>
                    </select>
                  </div>
                </div>
                <div className="col-3">
                  <div data-mdb-input-init className="form-outline mb-2">
                    <label className="form-label" for="typeNameX-2">Section</label>
                    <select className="form-select" aria-label="Default select example" value={sect} onChange={(e) => setSect(e.target.value)} required>
                      <option selected>Choose Your Section</option>
                      <option value="A">A</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                    </select>
                  </div>
                </div>
                <div className="col-3">
                  <div data-mdb-input-init className="form-outline mb-2">
                    <label className="form-label" for="typeNameX-2">Date</label>
                    <input type="date" id="typeEmailX-2" className="form-control" min="2023-01-01" value={date} onChange={(e) => setDate(e.target.value)} />
                  </div>
                </div>
              </div>
              <div className="d-flex justify-content-center mt-4">
              <button type="button" className="btn btn-danger mx-3" onClick={handleClear}>Clear</button>
              <Link type="button" to="/attendance" className="btn btn-warning mx-3">Add New</Link>
              <button type="submit" className="btn btn-success mx-3" >Show</button>
            </div>
            </form>
           


            {/* data display section */}
            <hr />
            <table className="table table-hover">
              <thead>
                <tr>
                  <th scope="col">Boys</th>
                  <th scope="col">Present</th>
                  <th scope="col">Girls</th>
                  <th scope="col">Present</th>
                </tr>
              </thead>
              <tbody>
                {
                  users.map(user => {
                    return <>
                      <tr>
                        <th scope="row">Hostel-Boys</th>
                        <td>{user.hBoys}</td>
                        <th scope="row">Hostel-Girls</th>
                        <td>{user.hGirls}</td>
                      </tr>
                      <tr>
                        <th scope="row">Day Scholar-Boys</th>
                        <td>{user.cbBoys}</td>
                        <th scope="row">Day Scholar-Girls</th>
                        <td>{user.cbGirls}</td>
                      </tr>
                      <tr>
                        <th scope="row">Out Bus-Boys</th>
                        <td>{user.obBoys}</td>
                        <th scope="row">Out Bus-Girls</th>
                        <td>{user.obGirls}</td>
                      </tr>
                      <tr>
                        <th scope="row">Total-Boys</th>
                        <td>{user.totBoys}</td>
                        <th scope="row">Total-Girls</th>
                        <td>{user.totGirls}</td>
                      </tr>
                    </>
                  })}
              </tbody>
            </table>

           


          </div>
        </div>
      </div>
    </div>
  )
}
