// import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';

export default function Preview() {
  const [dept, setDept] = useState('');
  const [sem, setSem] = useState('');
  const [sect, setSect] = useState('');
  const [date, setDate] = useState('');
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  axios.defaults.withCredentials = true;

  useEffe(() => {
    axios.get('http://localhost:3001/preview')
      .then(result => {
        if (result.data !== "success") {
          navigate('/');
        } else {
          setUsers(result.data.users); // Assuming the data structure has a `users` array
        }
      })
      .catch(err => console.log(err));
  }, [navigate]);

  const handleClear = () => {
    setDept('');
    setSem('');
    setSect('');
    setDate('');
  };

  return (
    <div>
      <div className="container h-100">
        <div className="row d-flex justify-content-center align-items-center h-100 m-3">
          <div className="card-body ">
            <hr />
            <form action="">
              <h2 className="mb-3 text-center">Student's Attendance Details</h2>
              <div className="row">
                <div className="col-3">
                  <div className="form-outline mb-2">
                    <label className="form-label" htmlFor="typeNameX-2">Department</label>
                    <select
                      className="form-select"
                      value={dept}
                      onChange={(e) => setDept(e.target.value)}
                      required
                    >
                      <option value="" disabled>Choose Department</option>
                      <option value="Aero">Aero</option>
                      <option value="Aeronautical">Aeronautical</option>
                      <option value="BME">BME</option>
                      <option value="CSE">CSE</option>
                      <option value="IT">IT</option>
                    </select>
                  </div>
                </div>
                <div className="col-3">
                  <div className="form-outline mb-2">
                    <label className="form-label" htmlFor="typeNameX-2">Semester</label>
                    <select
                      className="form-select"
                      value={sem}
                      onChange={(e) => setSem(e.target.value)}
                      required
                    >
                      <option value="" disabled>Choose Your Semester</option>
                      <option value="I">I</option>
                      <option value="II">II</option>
                      <option value="III">III</option>
                      <option value="IV">IV</option>
                      <option value="V">V</option>
                      <option value="VI">VI</option>
                      <option value="VII">VII</option>
                      <option value="VIII">VIII</option>
                    </select>
                  </div>
                </div>
                <div className="col-3">
                  <div className="form-outline mb-2">
                    <label className="form-label" htmlFor="typeNameX-2">Section</label>
                    <select
                      className="form-select"
                      value={sect}
                      onChange={(e) => setSect(e.target.value)}
                      required
                    >
                      <option value="" disabled>Choose Your Section</option>
                      <option value="A">A</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                    </select>
                  </div>
                </div>
                <div className="col-3">
                  <div className="form-outline mb-2">
                    <label className="form-label" htmlFor="typeEmailX-2">Date</label>
                    <input
                      type="date"
                      id="typeEmailX-2"
                      className="form-control"
                      value={date}
                      onChange={(e) => setDate(e.target.value)}
                      required
                    />
                  </div>
                </div>
              </div>
            </form>
            <div className="d-flex justify-content-center mt-4">
              <button type="button" className="btn btn-danger mx-3" onClick={handleClear}>Clear</button>
              <Link type="button" to="/attendence" className="btn btn-warning mx-3">Add New</Link>
              <button type="submit" className="btn btn-success mx-3">Show</button>
            </div>
            <hr />
            <table className="table table-hover">
              <thead>
                <tr>
                  <th scope="col">Boys</th>
                  <th scope="col">Present</th>
                  <th scope="col">Girls</th>
                  <th scope="col">Present</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user, index) => (
                  <tr key={index}>
                    <td>{user.hBoys}</td>
                    <td>{user.boysPresent}</td>
                    <td>{user.hGirls}</td>
                    <td>{user.girlsPresent}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
