import React, { useState } from 'react';

export default function Preview() {
  const [per, setPer] = useState('');

  let handleClear = () => {
    setPer(''); // Correct way to reset the state
  };

  return (
    <div>
      <select className="form-select" aria-label="Default select example" value={per} onChange={(e) => setPer(e.target.value)}>
        <option value="" disabled selected>Choose Period</option>
        <option value="I">I</option>
        <option value="II">II</option>
        <option value="III">III</option>
        <option value="IV">IV</option>
        <option value="V">V</option>
        <option value="VI">VI</option>
        <option value="VII">VII</option>
        <option value="VIII">VIII</option>
      </select>
      <button type="button" className="btn btn-danger mx-3" onClick={handleClear}>Clear</button>
    </div>
  );
}
