import React,  { useEffect } from 'react';
import { Link ,useNavigate} from 'react-router-dom';
import axios from 'axios';

export default function Dashboard() {

    axios.defaults.withCredentials = true;

    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:3001/verifyPreview/preview')
          .then(result => {
            console.log(result);
            if (result.data !== "success") {
              navigate('/');
            }
          })
          .catch(err => {
            console.log('Error during preview verification:', err);
            // Optional: You might want to navigate to an error page or handle it more gracefully
          });
      }, [navigate]); 
    return (
        <>
            <div className="row row-cols-1 row-cols-md-4 g-4 m-2">
                <div className="col">
                    <div className="card">
                        <img src="..." className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Department</h5>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card">
                        <img src="..." className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Total Student</h5>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card">
                        <img src="..." className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Total Boys</h5>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card">
                        <img src="..." className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Total Girls</h5>
                        </div>
                    </div>
                </div>
            </div>
            <div>
                <hr />
                <h2 className="mb-3 text-center">Student's Attendence Details</h2>
                <ul className="nav justify-content-center text-black">
                    <li className="nav-item text-center ">
                        <h1>Welcome Sagar Raj</h1>
                    </li>
                    <li className="nav-item text-center mx-3 mt-2">
                        <button className='btn btn-danger'>Log Out</button>
                    </li>
                </ul>
            </div>
            <br />
            <div className="d-flex justify-content-center mt-4">
                <Link to="/attendence" type="button" className="btn btn-danger  mx-3">Add New</Link>
                <Link to="/preview" type="submit" className="btn btn-success  mx-3" >Preview</Link>
            </div>


        </>
    )
}
